import styles from "./ChatContent.module.css";
import ChatChannelHeader from "./ChatChannelHeader";

const MESSAGE_DATA = [
  {
    messageId: 1,
    userId: 1,
    userName: 'user1',
    userAvatar: "https://i.pravatar.cc/300",
    messageTimeStamp: '2020-05-14T04:00:00Z',
    messageContent: "user1: 1st message",
    channelId: 1,
    channelName: "Channel1",
  },
  {
    messageId: 2,
    userId: 2,
    userName: 'user2',
    userAvatar: "https://i.pravatar.cc/301",
    messageTimeStamp: '2020-05-15T05:00:00Z',
    messageContent: "user2: 1st message",
    channelId: 2,
    channelName: "Channel2",
  },
  {
    messageId: 3,
    userId: 1,
    userName: 'user1',
    userAvatar: "https://i.pravatar.cc/300",
    messageTimeStamp: '2020-05-16T06:00:00Z',
    messageContent: "user1: 2nd message",
    channelId: 1,
    channelName: "Channel1",
  },
  {
    messageId: 4,
    userId: 2,
    userName: 'user2',
    userAvatar: "https://i.pravatar.cc/301",
    messageTimeStamp: '2020-05-17T07:00:00Z',
    messageContent: "user2: 2nd message",
    channelId: 2,
    channelName: "Channel2",
  },
];

type ChatContentProps = {
  activeChannelId: number;
}

function ChatContent(props: ChatContentProps) {

  // test date format
  const time = new Date().toISOString();
  console.log("current time is: " + time);
  console.log("time in milliseconds: " + +time);
  const active_channel_id = props.activeChannelId;
  return (
    <div>
      <ChatChannelHeader channelName={MESSAGE_DATA[active_channel_id].channelName} />
      {/*<ChannelMessages />*/}
    </div>
  );
}

export default ChatContent;
